package com.hluz.readings.application;import java.util.Arrays;public class DoubleExtensions {	public static double median(Number[] values) {		Arrays.sort(values);		int length = values.length;		if (length % 2 == 0) {			return (values[length / 2 - 1].doubleValue() + values[length / 2].doubleValue()) / 2;		} else {			return values[length / 2].doubleValue();		}	}}