package unit.readings.application;import static java.util.Arrays.asList;import static org.junit.jupiter.api.Assertions.assertEquals;import com.hluz.readings.application.DoubleExtensions;import java.util.ArrayList;import java.util.List;import lombok.experimental.ExtensionMethod;import org.junit.jupiter.api.Test;@ExtensionMethod({DoubleExtensions.class})public class DoubleExtensionsTest {	@Test	public void get_median_from_even_list() {		List<Double> values = new ArrayList<>(asList(1D, 2D));		double actual = values.median();		assertEquals(1.5D, actual);	}	@Test	public void get_median_from_odd_list() {		List<Double> values = new ArrayList<>(asList(1D, 2D, 3D));		double actual = values.median();		assertEquals(2D, actual);	}}