package unit.readings.application;import static org.junit.jupiter.api.Assertions.assertEquals;import com.hluz.readings.application.DoubleExtensions;import lombok.experimental.ExtensionMethod;import org.junit.jupiter.api.Test;@ExtensionMethod({DoubleExtensions.class})public class DoubleExtensionsTest {	@Test	public void get_median_from_even_list() {		Double[] values = new Double[2];		values[0] = 1D;		values[1] = 2D;		double actual = values.median();		assertEquals(1.5D, actual);	}	@Test	public void get_median_from_odd_list() {		Double[] values = new Double[3];		values[0] = 1D;		values[1] = 2D;		values[2] = 3D;		double actual = values.median();		assertEquals(2D, actual);	}}